// Adaptation pour PostgreSQL - les facts viennent directement de la requ√™te
const facts = $input.all().map(item => item.json.fact).filter(fact => fact);
return { json: { facts } };

// Adaptation pour PostgreSQL - structure simplifi√©e avec votre sch√©ma
const messages = $input.all();
const messagePairs = [];

for (const item of messages) {
    const message = item.json;
    const humanText = message.message || "";
    const aiText = message.response || "";

    if (humanText && aiText) {
        messagePairs.push(`Human: ${humanText}\nAI: ${aiText}`);
    } else if (humanText) {
        messagePairs.push(`Human: ${humanText}`);
    }
}

return { json: { conversations: messagePairs } };

// ‚ñº‚ñº‚ñº COLLEZ L'URL DE VOTRE MICRO-SERVICE ICI ‚ñº‚ñº‚ñº
const webhookUrl = 'https://gandas.ai/n8n/webhook/context-generator';
// ‚ñ≤‚ñ≤‚ñ≤ COLLEZ L'URL DE VOTRE MICRO-SERVICE ICI ‚ñ≤‚ñ≤‚ñ≤

if (!webhookUrl.startsWith('http')) {
  throw new Error("ERREUR : Veuillez coller l'URL de votre webhook de production dans le code.");
}

const item = $input.item.json;

// Use 'data' from Firebase flow or 'text' from Telegram flow
const fullText = item.data || item.text;
const fileId = item.file_id;
const fileTitle = item.file_title;
const userId = item.user_id; // Firebase user ID
const uploadSource = item.upload_source || 'unknown'; // web, telegram, api
const chatId = item.chat_id; // Pass through chat_id if exists
const chunkSize = 500;
const chunks = [];

// Handle cases where fullText might be null or undefined
if (fullText) {
    for (let i = 0; i < fullText.length; i += chunkSize) {
        chunks.push(fullText.substring(i, i + chunkSize));
    }
}

const enrichedData = [];

for (const chunk of chunks) {
  try {
    const response = await fetch(webhookUrl, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ chunk: chunk }),
    });

    if (!response.ok) {
      console.error(`Error from webhook: ${response.statusText}`);
      continue;
    }

    const hypotheticalQuestion = await response.json();
    const finalContent = `${hypotheticalQuestion.data}\n---\n${chunk}`;

    enrichedData.push({
      pageContent: finalContent,
      metadata: { 
        file_id: fileId, 
        file_title: fileTitle,
        user_id: userId,
        upload_source: uploadSource,
        processed_via: 'firebase_storage'
      }
    });
  } catch (error) {
    console.error(`Error calling webhook: ${error.message}`);
  }
}

const result = { 
  enrichedData: enrichedData,
  file_title: fileTitle, // Pass through file_title for confirmation message
  upload_source: uploadSource
}; 

// Pass through chat_id if it exists for the confirmation message
if (chatId) {
  result.chat_id = chatId;
}

// La ligne importante : tout retourner dans un seul champ
return [{ json: result }];

const supportedExtensions = ['.jpg', '.jpeg', '.png', '.webp', '.pdf', '.doc', '.docx', '.xls', '.xlsx', '.json', '.xml', '.txt', '.md'];
const fileName = $input.item.json.body.message.document.file_name || '';
const lowerName = fileName.toLowerCase();

const isSupported = supportedExtensions.some(ext => lowerName.endsWith(ext));

$input.item.json.is_supported = isSupported;

return $input.item;

